CREATE TABLE recipes ( _id INT PRIMARY KEY, book VARCHAR (255) NOT NULL, page INT NOT NULL, kitchen VARCHAR (255) NOT NULL, course VARCHAR (50) NOT NULL, title VARCHAR (255) NOT NULL, maxPreperationTime INT NOT NULL, persons INT NOT NULL, favorite INT NOT NULL, lactosefree INT NOT NULL, glutenfree INT NOT NULL, hide INT NOT NULL);
CREATE TABLE ingredients ( _id INT PRIMARY KEY, recipeId INT NOT NULL, name VARCHAR (50) NOT NULL, amount VARCHAR (50) NOT NULL, measure VARCHAR (50) NOT NULL, CONSTRAINT fkRecipeId FOREIGN KEY (recipeId) REFERENCES recipes (_id) ON DELETE CASCADE );
