CREATE TABLE recipes ( id INT PRIMARY KEY, book VARCHAR (255) NOT NULL, page INT NOT NULL, kitchen VARCHAR (255) NOT NULL, course VARCHAR (50) NOT NULL, title VARCHAR (255) NOT NULL, maxPreperationTime INT NOT NULL, persons INT NOT NULL, favorite INT NOT NULL, hide INT NOT NULL);
CREATE TABLE ingredients ( id INT PRIMARY KEY, recipeId INT NOT NULL, name VARCHAR (50) NOT NULL, amount VARCHAR (50) NOT NULL, measure VARCHAR (50) NOT NULL, CONSTRAINT fkRecipeId FOREIGN KEY (recipeId) REFERENCES recipes (id) ON DELETE CASCADE );
CREATE TABLE allergies ( id INT PRIMARY KEY, name VARCHAR (255) NOT NULL);
CREATE TABLE allergiesrecipes ( id INT PRIMARY KEY, recipeId INT NOT NULL, allergyId INT NOT NULL, CONSTRAINT fkRecipeIdAllergy FOREIGN KEY (recipeId) REFERENCES recipes (id), CONSTRAINT fkAllergyId FOREIGN KEY (allergyId) REFERENCES allergies (id) ON DELETE CASCADE );